(function($){
	$(document).ready(function () {
		// Dropdown menu
		if ($('.deeper').children('ul').length > 0) {
						$('.deeper > a').append('<b class="caret"></b>');
						$('.deeper > span').append('<b class="caret"></b>');

		}

		// Fix hide dropdown
		$('.dropdown-menu input, .dropdown-menu label').click(function(e) {
			e.stopPropagation();
		});
		// Tooltip
		$('.tooltip').tooltip({
			html: true
		});
		// To top
		var offset = 220;
		var duration = 500;
		$(window).scroll(function() {
			if ($(this).scrollTop() > offset) {
				$('.back-to-top').fadeIn(duration);
			} else {
				$('.back-to-top').fadeOut(duration);
			}
		});
		$('.back-to-top').click(function(event) {
			event.preventDefault();
			$('html, body').animate({scrollTop: 0}, duration);
			return false;
		});
	});


	$(function(){
		$('.navigation .menu ').slicknav({
			prependTo:'#navigation .navbar',
			label: ''
			}
		);
	});
	$("img.scale").imageScale();
	$(".leading-blog-item img").imageScale();


	if ($(window).width() > 480) {
			$('.blog-item-margin').matchHeight();
		}
	$('.jn-item').matchHeight();


		if ($(window).width() > 768) {
	$(function() {
    //caches a jQuery object containing the header element
    var header = $("#header");
	$(window).on("load resize scroll",function(e){
		var scroll = $(window).scrollTop();

        if (scroll >= 130) {
            header.removeClass('clearHeader').addClass("darkHeader");
        } else {
            header.removeClass("darkHeader").addClass('clearHeader');
        }
    });


});
}




	$(document).ready(function(){
					var submitIcon = $('.searchbox-icon');
					var inputBox = $('#mod-search-searchword');
					var searchBox = $('.searchbox');
					var search = $('.top-search');
					var isOpen = false;
					submitIcon.click(function(){
							if(isOpen == false){
									search.addClass('search-open');
									searchBox.addClass('searchbox-open');
									inputBox.focus();
									isOpen = true;
							} else {
									search.removeClass('search-open');
									searchBox.removeClass('searchbox-open');
									inputBox.focusout();
									isOpen = false;
							}
					});
					 submitIcon.mouseup(function(){
									return false;
							});
					searchBox.mouseup(function(){
									return false;
							});
					$(document).mouseup(function(){
									if(isOpen == true){
											$('.searchbox-icon').css('display','block');
											submitIcon.click();
									}
							});
			});
					function buttonUp(){
							var inputVal = $('.searchbox-input').val();
							inputVal = $.trim(inputVal).length;
							if( inputVal !== 0){
									$('.searchbox-icon').css('display','none');
							} else {
									$('.searchbox-input').val('');
									$('.searchbox-icon').css('display','block');
							}
					}

})(jQuery);
